import { ErrorMessage, Field, Form, Formik } from "formik";
import React from "react";
import { useNavigate } from "react-router";
import * as Yup from "yup";

const loginSchema = Yup.object().shape({
  email: Yup.string()
    .email("invalid email format")
    .required("email is required"),
  password: Yup.string().required("password is required"),
});

const LoginFormik = ({ setlogged }) => {
  const navigate = useNavigate();
  const initialCredentials = {
    email: "",
    password: "",
  };

  return (
    <div>
      <Formik
        //intial values
        initialValues={initialCredentials}
        //YUP validation schema
        validationSchema={loginSchema}
        //VALUES toma los valores que son enviados desde el formulario
        onSubmit={async (values) => {
          await new Promise((r) => setTimeout(r, 2000));
          alert(JSON.stringify(values, null, 2));
          setlogged(true);
          /*we save the data in the LOCALSTORAGE */
          localStorage.setItem("credentials", values);
          navigate("/Profile");
        }}
      >
        {/*obtenemos props de formik, estos son aportados por la misma etiqueta */}
        {({
          values,
          touched,
          errors,
          isSubmitting,
          handleChange,
          handleBlur,
        }) => (
          <Form>
            <label htmlFor="email">Email</label>
            <Field
              id="email"
              name="email"
              placeHolder="ejemplo@gmail.com"
              type="email"
              required
            ></Field>

            {/*aqi manejo los errores del mail */}
            {errors.email && touched.email && (
              <div>
                <ErrorMessage name="email"></ErrorMessage>
              </div>
            )}

            <label htmlFor="password">Password</label>
            <Field
              id="password"
              name="password"
              placeHolder="password"
              type="password"
            ></Field>
            {/*aqi manejo los errores del password */}
            {errors.password && touched.password && (
              <div>
                <ErrorMessage name="password"></ErrorMessage>
              </div>
            )}
            <button type="submit">Login</button>
            {/*este es un prop que me captura el estado de la carga(isSubmittin:boolean) */}
            {isSubmitting ? <p>login your credentials...</p> : null}
          </Form>
        )}
      </Formik>
    </div>
  );
};

export default LoginFormik;
