import React, { useState } from "react";
//import TaskComponent from "../Tasks/TaskComponent";
import AddTaskFormik from "../Form/AddTaskFormik";
import TaskListComponent from "../Tasks/TaskListComponent";
import { LEVELS } from "../Tasks/levels.enum";
import { Task } from "../Tasks/task.class";

const TaskCard = () => {
  const defaultTask1 = new Task(
    "Estudiar React",
    "a darle atomos",
    true,
    LEVELS.URGENT
  );
  const [Tasks, setTasks] = useState([defaultTask1]);

  const addTask = (task) => {
    console.log("Task added:", task);

    setNewTask(task);
    /*esta funcion me AGREGA una tarea desde el FORM que esta en la parte inferior del formulario
        , esta funcion se llama desde TASKFORM*/
  };
  return (
    <div>
      TaskCard, aqui se renderiza TaskComponet y TaskForm
      <div className="card">
        <div className="card-header">card-header</div>
        <div className="card-body">
          card-body<TaskListComponent newtask={newTask}></TaskListComponent>
        </div>
        <div className="card-footer">
          card-footer<AddTaskFormik add={addTask}></AddTaskFormik>
        </div>
      </div>
    </div>
  );
};

export default TaskCard;
