import React from "react";
import { ErrorMessage, Field, Form, Formik } from "formik";
import * as Yup from "yup";
import { LEVELS } from "../../../models/levels.enum";
import { Task } from "../../../models/task.class";

const AddTaskFormik = () => {
  const initialCredentials = {
    name: "",
    descriptions: "",
    levels: LEVELS.NORMAL,
  };
  const taskSchema = Yup.object().shape({
    name: Yup.string().required("Ingrese nombre tarea"),
    descriptions: Yup.string().required("Ingrese descripcion"),
    levels: Yup.string()
      .oneOf(
        [LEVELS.NORMAL, LEVELS.URGENT, LEVELS.BLOCKING],
        "you mut select a TaskLevel: Normal/Urgent/Blocking"
      )
      .required("level  is required"),
  });

  return (
    <div>
      <Formik
        initialValues={initialCredentials}
        validationSchema={taskSchema}
        onSubmit={async (values) => {
          await new Promise((r) => setTimeout(r, 500));
          //alert(JSON.stringify(values, null, 2));
          // console.log(values);
          // console.log(values.name);
          const newTask = new Task(
            values.name,
            values.descriptions,
            false,
            values.levels
          );
          add(newTask);
        }}
      >
        {({
          values,
          touched,
          errors,
          isSubmitting,
          handleChange,
          handleBlur,
        }) => (
          <Form>
            <label htmlFor="name">nombre de la Tarea</label>
            <Field
              id="name"
              className="form-control"
              name="name"
              placeholder="ingrese nombre de la tarea"
              type="text"
              required
            />
            {/*aqi manejo los errores del email */}
            {errors.name && touched.name && (
              <ErrorMessage name="name" component="div" />
            )}

            <label htmlFor="descriptions">descripcion de la Tarea</label>
            <Field
              id="descriptions"
              className="form-control"
              name="descriptions"
              placeholder="ingrese descripcion de la tarea"
              type="text"
              required
            />
            {/*aqi manejo los errores del email */}
            {errors.descriptions && touched.descriptions && (
              <ErrorMessage name="descriptions" component="div" />
            )}

            <select
              id="levels"
              className="form-control"
              defaultValue={LEVELS.NORMAL}
            >
              <option value={LEVELS.NORMAL}>Normal</option>
              <option value={LEVELS.URGENT}>Urgent</option>
              <option value={LEVELS.BLOCKING}>Blocking</option>
            </select>
            <button type="submit">add Task</button>
          </Form>
        )}
      </Formik>
    </div>
  );
};

export default AddTaskFormik;
