import React from "react";
import { ErrorMessage, Field, Form, Formik } from "formik";
import * as Yup from "yup";
import { LEVELS } from "../../components/Tasks/levels.enum";
import { Task } from "../Tasks/task.class";

const AddTaskFormik = ({ add }) => {
  const initialCredentials = {
    titulo: "",
    descripcion: "",
    levels: LEVELS.NORMAL,
  };
  const taskSchema = Yup.object().shape({
    titulo: Yup.string().required("Ingrese titulo de la tarea"),
    descricion: Yup.string().required("Ingrese descripcion"),
    levels: Yup.string()
      .oneOf(
        [LEVELS.NORMAL, LEVELS.URGENT, LEVELS.BLOCKING],
        "you mut select a TaskLevel: Normal/Urgent/Blocking"
      )
      .required("level  is required"),
  });
  const handelSubmit = (values) => {
    console.log("estoy en formik");
    console.log(values);
    const newTask = new Task(
      values.titulo,
      values.descripcion,
      false,
      values.levels
    );
    add(newTask);
  };

  return (
    <div>
      <Formik
        initialValues={initialCredentials}
        validationSchema={taskSchema}
        // onSubmit={(values) => {
        //   console.log(values.titulo);
        // }}
        // (values) => {
        // await new Promise((r) => setTimeout(r, 500));
        //alert(JSON.stringify(values, null, 2));
        // console.log(values);
        // console.log(values.name);
        // const newTask = new Task(
        //   values.titulo,
        //   values.descripcion,
        //   false,
        //   values.levels
        // );
        // console.log("en formik");
        // console.log(newTask);
        // add(newTask);
        // }
      >
        {({
          values,
          touched,
          errors,
          isSubmitting,
          handleChange,
          handleBlur,
        }) => (
          <Form>
            <label htmlFor="titulo">nombre de la Tarea</label>
            <Field
              id="titulo"
              className="form-control"
              name="titulo"
              placeholder="ingrese titulo de la tarea"
              type="text"
              required
            />
            {/*aqi manejo los errores del email */}
            {errors.titulo && touched.titulo && (
              <ErrorMessage name="titulo" component="div" />
            )}
            <label htmlFor="descripcion">descripcion de la Tarea</label>
            <Field
              id="descripcion"
              className="form-control"
              name="descripcion"
              placeholder="ingrese descripcion de la tarea"
              type="text"
              required
            />
            {/*aqi manejo los errores del email */}
            {errors.descripcion && touched.descripcion && (
              <ErrorMessage name="descripcion" component="div" />
            )}
            <select
              id="levels"
              className="form-control"
              defaultValue={LEVELS.NORMAL}
            >
              <option value={LEVELS.NORMAL}>Normal</option>
              <option value={LEVELS.URGENT}>Urgent</option>
              <option value={LEVELS.BLOCKING}>Blocking</option>
            </select>
            <button onClick={() => handelSubmit(values)}>add Task</button>
          </Form>
        )}
      </Formik>
    </div>
  );
};

export default AddTaskFormik;
